import Image from "next/image";
import React, { useRef, useState } from "react";

import type { Meta, StoryObj } from "@storybook/react";
import Accessibility from "./assets/accessibility.svg";
import AvifImage from "./assets/avif-test-image.avif";

const meta = {
  component: Image,
  args: {
    src: Accessibility,
    alt: "Accessibility",
  },
} satisfies Meta<typeof Image>;

export default meta;

type Story = StoryObj<typeof meta>;

export const Default = {} satisfies Story;

export const Avif = {
  args: {
    src: AvifImage,
    alt: "Avif Test Image",
  },
} satisfies Story;

export const BlurredPlaceholder = {
  args: {
    placeholder: "blur",
  },
} satisfies Story;

export const BlurredAbsolutePlaceholder = {
  args: {
    src: "https://storybook.js.org/images/placeholders/50x50.png",
    width: 50,
    height: 50,
    blurDataURL:
      "data:image/png;base64,",
    placeholder: "blur",
  },
  parameters: {
    // ignoring in Chromatic to avoid inconsistent snapshots
    // given that the switch from blur to image is quite fast
    chromatic: { disableSnapshot: true },
  },
} satisfies Story;

export const FilledParent = {
  args: {
    fill: true,
  },
  decorators: [
    (Story) => (
      <div style={{ width: 500, height: 500, position: "relative" }}>
        {Story()}
      </div>
    ),
  ],
} satisfies Story;

export const Sized = {
  args: {
    fill: true,
    sizes: "(max-width: 600px) 100vw, 600px",
  },
  decorators: [
    (Story) => (
      <div style={{ width: 800, height: 800, position: "relative" }}>
        {Story()}
      </div>
    ),
  ],
} satisfies Story;

export const Lazy = {
  args: {
    src: "https://storybook.js.org/images/placeholders/50x50.png",
    width: 50,
    height: 50,
  },
  decorators: [
    (Story) => (
      <>
        <div style={{ height: "200vh" }} />
        {Story()}
      </>
    ),
  ],
} satisfies Story;

export const Eager = {
  ...Lazy,
  parameters: {
    nextjs: {
      image: {
        loading: "eager",
      },
    },
  },
} satisfies Story;

export const WithRef = {
  render() {
    const [ref, setRef] = useState<HTMLImageElement | null>(null);

    return (
      <div>
        <Image src={Accessibility} alt="Accessibility" ref={setRef} />
        <p>Alt attribute of image: {ref?.alt}</p>
      </div>
    );
  },
} satisfies Story;
